import React, {useState, useEffect, useRef} from 'react'

const Typewriter = () => {
    let text = ['Web Developer', 'Problem Solver', 'Team Player'];
    // let word = []
    // let textIndex = 0;

    let i = 0;
    let j = 0;

    const [currentWord, setCurrentWord] = useState('')
    // const charIndex = useRef(0);
    // const textIndex = useRef(0);
    const [isDeleting, setIsDeleting] = useState(false)

    function loop () {
        setIsDeleting(false);
        setCurrentWord(word.join(''))

        if (textIndex < text.length) {
            // if we're typing and we haven't completed the word
            if (!isDeleting && j <= text[textIndex].length) {
                word.push(text[textIndex][charIndex]);
                charIndex++;
                setCurrentWord(word.charIndexoin(''));
            }

            if(isDeleting && charIndex <= text[textIndex].length) {
                word.pop(text[textIndex][charIndex]);
                charIndex--;
                setCurrentWord(word.join(''));
            }

            if (charIndex == text[textIndex].length) {
                setIsDeleting(true);
            }

            if (isDeleting && charIndex === 0) {
                word = [];
                setIsDeleting(false);
                textIndex++;
                if(textIndex === word.length) {
                    textIndex = 0;
                }
            }
        }
       
    }

    useEffect(() => {
        setTimeout(() => loop(), 300)
        // return (() => {
        //     clearTimeout(timeoutID);
        // })
    }, [currentWord, text])


    return (
        <div>
            <h3>{currentWord}</h3>
            {/* <h3>hello</h3> */}
        </div>
    )
}

export default Typewriter
