import React, { useState } from 'react'

const ProjectNav = ({categories, filterCategories}) => {
    const [value, setValue] = useState(0)

    return (
        <ul>
            {categories.map((category, index) => {
                return <li><p className={`${index === value && 'active-btn'}`} key={index} onClick={() => 
                    {filterCategories(category);
                    setValue(value);
                }}>{category}</p></li>
            })}
        </ul>
    )
}

export default ProjectNav
