import React from 'react'

const Hero = () => {
    const [currentText, setCurrentText] = useState('');

    useEffect(() => {
        setCurrentText((value) => value + 'A');
    }, [])

    return (
        <section id='hero'>
            <h1>hi, I'm Adriana</h1>
            <h2></h2>
             <ul class="social-links btn">
                <li><a class="btn btn-1" href="mailto:adriana.calvomatos@gmail.com?"><i class="far fa-envelope fa-2x"></i></a></li>
                <li><a class="btn btn-2" href="https://github.com/wavyadri" role="button" target="_blank"><i class="fab fa-github fa-2x"></i></a></li>
                <li><a class="btn btn-3" href="https://www.linkedin.com/in/adriana-cm/" role="button" target="_blank"><i class="fab fa-linkedin fa-2x"></i></a></li>
            </ul>
        </section>
    )
}

export default Hero
