    let textIndex = 0;

    const [currentWord, setCurrentWord] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [isDeleting, setIsDeleting] = useState(false);

    const typing = () => {
        console.log('typing is running')
        setCurrentWord(`${text[textIndex].slice(0, count)}`)
        count++;
        console.log(currentWord)
        if(count === text[textIndex].length) {
            setIsDeleting(true)
            setIsTyping(false)
        }
        
    }

    if (isTyping) {
        typing();
    }

    const deleting = () => {
        console.log('deleting is running')
        setCurrentWord('')
    }

    if (isDeleting) {
        deleting();
    }

    useEffect(() => {
        setIsDeleting(false);
        setIsTyping(true);
        // L O O P
        // if (textIndex >= text.length) {
        //     textIndex = 0;
        // }

    }, [])

    useEffect(() => {
        setInterval(typing, 250)
    }, [])

    

