    //     setCurrentWord(characters);
    //     count++;
    // }

    // useEffect(() => {
    //     setInterval(typeEffect, 250)
    // }, [])
    return (
        <div>
            <h3>{currentWord}</h3>
            <h3>hello</h3>
        </div>
    )
}

