import React, { useState } from 'react'

const ProjectNav = ({projectsList, setProjectsList}) => {
    // remove duplicates
    const newCategories = ['all',...new Set(projectsList.map((item) => item.category))]

    const [categories, setCategories] = useState(newCategories)
    
    const filterCategories = (category) => {
        if(category === 'all') {
            setProjectsList(projectsList);
            return;
        }
        const newCategory = projectsList.filter((item) => item.category === category);
        setProjectsList(newCategory);
    }

    return (
        <ul style={{color: 'white'}}>
            {categories.map((category, index) => {
                return <li><p key={index} onClick={() => filterCategories(category)}>{category}</p></li>
            })}
        </ul>
    )
}

export default ProjectNav
