import React, {useState} from 'react'
import ProjectNav from './ProjectNav'
import {projects} from './data'
import {FiExternalLink} from 'react-icons/fi'

// remove duplicates
const newCategories = ['all',...new Set(projects.map((item) => item.category))]

const Projects = () => {
    const [projectsList, setProjectsList] = useState(projects);
    const [categories, setCategories] = useState(newCategories);
    
    const filterCategories = (category) => {
        if(category == 'all') {
            setProjectsList(projects);
            return;
        }
        const newCategory = projects.filter((item) => item.category === category);
        setProjectsList(newCategory);
    }

    return (
        <section id='projects'>
            <div className="container">
                <h2>Projects</h2>
                <ProjectNav categories={categories} filterCategories={filterCategories}/>
                <div className="projects-grid">
                    {projectsList.map((item) => {
                    const {id, image, title, description, category, url} = item;
                    return <div key={id} className="card" style={{backgroundImage: `url(${image})`}}>
                        <div className="card-overlay">
                            <div className="card-icon">
                                <a href={url}><FiExternalLink className='icon'/></a>
                            </div>
                            <div className="card-text">
                                <h2><a href={url}>{description}</a></h2>
                                <p>{category}</p>    
                            </div>
                        </div>
                    </div>
                    })}
                </div>
            </div>
        </section>
    )
}

export default Projects
