import React, {useState, useEffect, useRef} from 'react'
import Typewriter from './Typewriter';

const Hero = () => {
    let text = ['Web Developer', 'Problem Solver', 'Team Player'];
    let count = 1;
    let wordNum = 0;

    const [currentWord, setCurrentWord] = useState('');

    const typeEffect = () => {
        if(count > text[wordNum].length) {
            wordNum++;
            count = 0;
        }
        if(wordNum === text.length) {
            wordNum = 0;
        }
        const characters = text[wordNum].slice(0, count);
        setCurrentWord(characters);
        count++;
    }

    useEffect(() => {
        setInterval(typeEffect, 250)
    }, [])


    return (
        <section id='hero'>
            <h1>hi, I'm Adriana</h1>
            <h2 className='type-text'><Typewriter/></h2>
             <ul class="social-links btn">
                <li><a class="btn btn-1" href="mailto:adriana.calvomatos@gmail.com?"><i class="far fa-envelope fa-2x"></i></a></li>
                <li><a class="btn btn-2" href="https://github.com/wavyadri" role="button" target="_blank"><i class="fab fa-github fa-2x"></i></a></li>
                <li><a class="btn btn-3" href="https://www.linkedin.com/in/adriana-cm/" role="button" target="_blank"><i class="fab fa-linkedin fa-2x"></i></a></li>
            </ul>
        </section>
    )
}

export default Hero
