import React, { useState } from 'react'

const ProjectNav = ({dataItems}) => {
    console.log(dataItems)
    // remove duplicates
    const newCategories = ['all',...new Set(dataItems.map((item) => item.category))]
    console.log(newCategories)

    // const [categories, setCategories] = useState(newCategories)

    return (
        <nav style={{color: 'white'}}>
            {newategories.map((category, index) => {
                return <h1 key={index}>{category}</h1>
            })}
        </nav>
    )
}

export default ProjectNav
